<style>
.main{
  overflow-y: scroll;
  scroll-snap-type: y mandatory;
  display: grid;
  grid-template-rows: repeat(2, 100vh);
}

.section-1{
  height: 100vh;
  width: 100%;
  padding: 1rem;
  display: grid;
  grid-auto-rows: auto repeat(3, 1fr);

  .info-section-1{
    margin-left: auto;
    margin-right: auto;
    margin-top: auto;
    margin-bottom: auto;

    p{
      margin-top: 1rem;
      max-width: 18.75rem;
    }
  }
}

.social-media-container{
  display: flex;
  flex-direction: row;
  width: 100%;
  gap: 1rem;
}

.social-media-svg{
  width: clamp(3.125rem, 10vw, 25rem);
  margin-left: auto;
  margin-right: auto;
}

.section-2{
  height: 100vh;
  width: 100%;
  padding: 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;

  .section-2-header{
    width: 100%;
    text-align: center;
  }

  .info-section-2{
    max-width: 28.75rem;
  }

  .info-container-section-2{
    display: flex;
    text-align: center;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
  }
}

.circle-section-1-container{
  display: flex;
  justify-content: center;
  align-items: center;
}

svg {
  overflow: visible;
}

.text {
  transform-box: fill-box;
  transform-origin: center;
  animation: rotateLogo 10s linear infinite;
}

@keyframes rotateLogo {
  to {
    transform: rotate(1turn);
  }
}

h1{
  width: 100%;
  text-wrap: nowrap;
  display: flex;
  justify-content: center;
  height: fit-content;
}

@media (min-width: 50rem) { /* 800px */
  .section-1{
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: repeat(3, 1fr);
    padding: 0;
    padding-left: 1rem;
    padding-right: 1rem;
    padding-top: 1rem;
  }

  h1{
    grid-column: 1/8;
    position: absolute;
  }

  .info-section-1{
    grid-column: 1/4;
    grid-row: 2/3;
  }

  .circle-section-1-container{
    grid-column: 7/9;
    grid-row: 2/3;
  }

  :global(.mugshot-img) {
    grid-row: 1/4;
    grid-column: 2/8;
    z-index: 99;
    position: relative;

    :global(img){
